import React, { useState } from 'react';
import { Pie, Bar } from 'react-chartjs-2';
import './ChurnPrediction.css';

function ChurnPrediction() {
  const [formData, setFormData] = useState({
    customerID: '',
    age: '',
    accountType: 'Checking',
    accountBalance: '',
    transactionFrequency: '',
    serviceUsage: 'Yes',
    customerSupport: '',
    maritalStatus: 'Single',
    gender: 'Male',
  });

  const [prediction, setPrediction] = useState({
    churnProbability: null,
    retentionProbability: null,
    recommendations: '',
  });

  const handleChange = (e) => {
    const { id, value } = e.target;
    setFormData({ ...formData, [id]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Dummy prediction logic
    const churnProbability = Math.random() * 100;
    const retentionProbability = 100 - churnProbability;
    setPrediction({
      churnProbability: churnProbability.toFixed(2),
      retentionProbability: retentionProbability.toFixed(2),
      recommendations: 'Consider offering special incentives or discounts to retain this customer.',
    });
  };

  // Dummy data for visualization
  const pieData = {
    labels: ['Retained', 'Churned'],
    datasets: [
      {
        label: 'Proportion of Customer Churn',
        data: [79.6, 20.4],
        backgroundColor: ['#FF6384', '#36A2EB'],
        hoverBackgroundColor: ['#FF6384', '#36A2EB'],
      },
    ],
  };

  const barData = {
    labels: ['Age', 'Account Balance', 'Transaction Frequency'],
    datasets: [
      {
        label: 'Variable Contributions',
        data: [0.25, -0.15, 0.20],
        backgroundColor: ['#FFCE56', '#FF6384', '#36A2EB'],
        borderColor: ['#FFCE56', '#FF6384', '#36A2EB'],
        borderWidth: 1,
      },
    ],
  };

  return (
    <div className="churn-prediction-container">
      <header className="header">
        <h1>Customer Churn Prediction</h1>
        <p>Analyze customer data to predict churn probability and improve retention strategies.</p>
      </header>
      <div className="form-section">
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="customerID">Customer ID:</label>
            <input type="text" id="customerID" value={formData.customerID} onChange={handleChange} />
          </div>
          <div className="form-group">
            <label htmlFor="age">Age:</label>
            <input type="number" id="age" value={formData.age} onChange={handleChange} />
          </div>
          <div className="form-group">
            <label htmlFor="accountType">Account Type:</label>
            <select id="accountType" value={formData.accountType} onChange={handleChange}>
              <option value="Checking">Checking</option>
              <option value="Savings">Savings</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="accountBalance">Account Balance:</label>
            <input type="number" id="accountBalance" value={formData.accountBalance} onChange={handleChange} />
          </div>
          <div className="form-group">
            <label htmlFor="transactionFrequency">Transaction Frequency:</label>
            <input type="number" id="transactionFrequency" value={formData.transactionFrequency} onChange={handleChange} />
          </div>
          <div className="form-group">
            <label htmlFor="serviceUsage">Online Banking Usage:</label>
            <select id="serviceUsage" value={formData.serviceUsage} onChange={handleChange}>
              <option value="Yes">Yes</option>
              <option value="No">No</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="customerSupport">Customer Support:</label>
            <input type="text" id="customerSupport" value={formData.customerSupport} onChange={handleChange} />
          </div>
          <div className="form-group">
            <label htmlFor="maritalStatus">Marital Status:</label>
            <select id="maritalStatus" value={formData.maritalStatus} onChange={handleChange}>
              <option value="Single">Single</option>
              <option value="Married">Married</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="gender">Gender:</label>
            <select id="gender" value={formData.gender} onChange={handleChange}>
              <option value="Male">Male</option>
              <option value="Female">Female</option>
            </select>
          </div>
          <button type="submit" className="predict-button">Predict Churn</button>
        </form>
      </div>
      <div className="analysis-summary">
        <h2>Prediction Summary</h2>
        {prediction.churnProbability !== null && (
          <div>
            <p>Churn Probability: {prediction.churnProbability}%</p>
            <p>Retention Probability: {prediction.retentionProbability}%</p>
            <p>Recommendations: {prediction.recommendations}</p>
          </div>
        )}
      </div>
      <div className="charts-section">
        <h2>Churn Analysis</h2>
        <Pie data={pieData} />
        <Bar data={barData} />
      </div>
      <div className="historical-data">
        <h2>Historical Data Insights</h2>
        {/* Add additional insights or historical data charts */}
      </div>
    </div>
  );
}

export default ChurnPrediction;
